/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `ch_trans` (
  `ct_id` int(11) NOT NULL auto_increment,
  `ct_text` text NOT NULL,
  `ct_translator` varchar(255) default NULL,
  `ct_publisher` varchar(255) default NULL,
  `ct_pub_date` varchar(255) default NULL,
  `jp_num` int(11) NOT NULL,
  PRIMARY KEY  (`ct_id`),
  KEY `ct_fk` (`jp_num`),
  CONSTRAINT `ct_fk` FOREIGN KEY (`jp_num`) REFERENCES `jp_ori` (`jp_id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=1727 DEFAULT CHARSET=utf8;
/*!40101 SET character_set_client = @saved_cs_client */;

